import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';

const Auth = ({ onLogin }) => {
  const [isSignup, setIsSignup] = useState(false);
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [showGuide, setShowGuide] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    if (token) {
      localStorage.setItem('token', token);
      const fetchUser = async () => {
        try {
          const res = await api.get('/profile');
          localStorage.setItem('user', JSON.stringify(res.data));
          onLogin(res.data);
          navigate('/dashboard', { replace: true });
        } catch (err) { alert("Error fetching user"); }
      };
      fetchUser();
    }
  }, [onLogin, navigate]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const endpoint = isSignup ? '/signup' : '/login';
      const res = await api.post(endpoint, formData);
      
      if (isSignup) {
        alert('Account created successfully! Please log in.');
        setIsSignup(false);
      } else {
        localStorage.setItem('token', res.data.accessToken);
        localStorage.setItem('user', JSON.stringify(res.data.user));
        onLogin(res.data.user);
      }
    } catch (err) {
      alert(isSignup ? 'Signup Failed' : 'Login Failed');
    } finally {
      setLoading(false);
    }
  };

  const handleKeycloak = () => {
    window.location.href = `http://localhost:8080/realms/r-realm/protocol/openid-connect/auth?client_id=r-client&redirect_uri=http://localhost:3000/auth/callback&response_type=code&scope=openid email profile`;
  };

  // Password Reset Logic
  const handleForgotPassword = async () => {
    const email = prompt("Please enter your email address:");
    if (!email) return;

    try {
      const res = await api.post('/forgot-password', { email });
      
      // Simulate Email by showing alert
      alert(`ðŸ”‘ PASSWORD RESET TOKEN: ${res.data.token}\n\nThis token would normally be emailed to you.`);
      
      // Prompt for New Password & Token
      const newPassword = prompt("Enter your NEW password:");
      if (!newPassword) return;
      
      const token = prompt("Paste the RESET TOKEN shown above:");
      if (!token) return;

      const resetRes = await api.post('/reset-password', { token, newPassword });
      
      if (resetRes.data.message === 'Password reset successfully') {
        alert("Success! You can now log in with your new password.");
      }
    } catch (err) {
      alert("Error: " + (err.response?.data?.message || err.message));
    }
  };

  return (
    <div className="auth-wrapper">
      {/* Art Side */}
      <div className="auth-art">
        <div style={{ position: 'relative', zIndex: 2, textAlign: 'center' }}>
          <div style={{ marginBottom: '1rem' }}>
            <svg width="80" height="80" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round">
              <path d="M12 22s8-4 8-10V5l-8-3-8 3v12s8 4 8 10z"/>
              <path d="M8 11h3a2 2 0 0 0 2-2v-4a2 2 0 0 0-2-2h-2a2 2 0 0 0-2 2v1a2 2 0 0 0 2 2h3z"/>
              <path d="M9 7h.01"/>
            </svg>
          </div>
          <h1 style={{ fontSize: '3.5rem', fontWeight: 800, marginBottom: '0.5rem', lineHeight: 1.1, letterSpacing: '-0.03em' }}>TrustAuth</h1>
          <p style={{ fontSize: '1.1rem', opacity: 0.9, fontWeight: 500 }}>Secure Identity Management</p>
        </div>
      </div>

      {/* Form Side */}
      <div className="auth-form-container">
        <div className="auth-box">
          <div className="auth-title">{isSignup ? 'Create Account' : 'Welcome Back'}</div>
          <div className="auth-sub">{isSignup ? 'Enter your details to join our platform' : 'Please enter your credentials to access your account'}</div>

          {/* OAuth Buttons */}
          <button className="btn btn-primary" style={{ width: '100%', marginBottom: '0.75rem' }} onClick={handleKeycloak}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M15 3h4a2 2 0 0 1 2 2v14a2 2 0 0 1-2 2h-4"/><polyline points="10 17 15 12 10 7"/><line x1="15" y1="12" x2="3" y2="12"/></svg>
            Log in with Keycloak
          </button>
          
          <button 
            className="btn" 
            style={{ width: '100%', marginBottom: '1rem', fontSize: '0.85rem', color: '#64748b', border: '1px dashed #cbd5e1', background: 'transparent' }}
            onClick={() => setShowGuide(true)}
          >
            ðŸ“š Need help with Keycloak?
          </button>

          <div className="divider"><span>OR</span></div>

          <form onSubmit={handleSubmit}>
            {isSignup && (
              <div className="input-group">
                <label>Full Name</label>
                <input name="username" type="text" placeholder="e.g., John Doe" onChange={handleChange} required />
              </div>
            )}
            <div className="input-group">
              <label>Email Address</label>
              <input name="email" type="email" placeholder="name@company.com" onChange={handleChange} required />
            </div>
            <div className="input-group">
              <label>Password</label>
              <input name="password" type="password" placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢" onChange={handleChange} required />
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1.5rem', fontSize: '0.9rem', fontWeight: 500 }}>
              <label style={{ display: 'flex', alignItems: 'center', gap: '8px', cursor: 'pointer' }}>
                <input type="checkbox" /> Remember me
              </label>
              <button type="button" onClick={handleForgotPassword} style={{ background: 'none', border: 'none', color: 'var(--primary)', fontWeight: 600, cursor: 'pointer', textDecoration: 'underline' }}>
                Forgot Password?
              </button>
            </div>

            <button type="submit" className="btn btn-primary" style={{ width: '100%' }} disabled={loading}>
              {loading ? (
                <span style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                  <span style={{ width: '16px', height: '16px', borderRadius: '50%', border: '2px solid rgba(255,255,255,0.3)', borderBottom: '2px solid transparent', animation: 'spin 1s linear infinite' }}></span> Processing...
                </span>
              ) : (isSignup ? 'Create Account' : 'Sign In')}
            </button>
          </form>

          <div style={{ marginTop: '2.5rem', textAlign: 'center', fontSize: '0.9rem', color: '#64748b' }}>
            {isSignup ? "Already have an account? " : "Don't have an account? "}
            <span onClick={() => setIsSignup(!isSignup)} style={{ color: 'var(--primary)', fontWeight: 700, cursor: 'pointer', textDecoration: 'underline', textUnderlineOffset: '4px' }}>
              {isSignup ? 'Log in' : 'Sign up'}
            </span>
          </div>
        </div>
      </div>

      {/* Keycloak Guide Modal */}
      {showGuide && (
        <div style={{ 
            position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', 
            background: 'rgba(15, 23, 42, 0.6)', zIndex: 100, 
            display: 'grid', placeItems: 'center', backdropFilter: 'blur(5px)', animation: 'fadeIn 0.2s' 
          }}>
          <div className="card" style={{ width: '100%', maxWidth: '600px', padding: '2.5rem', maxHeight: '80vh', overflowY: 'auto', position: 'relative', borderRadius: '20px' }}>
            <button 
              onClick={() => setShowGuide(false)} 
              style={{ position: 'absolute', top: '1.5rem', right: '1.5rem', background: 'none', border: 'none', fontSize: '1.8rem', cursor: 'pointer', color: '#94a3b8', lineHeight: 1 }}
            >Ã—</button>

            <h2 style={{ fontSize: '2rem', fontWeight: 800, marginBottom: '0.5rem', color: '#0f172a' }}>Keycloak Guide</h2>
            <p style={{ color: '#64748b', marginBottom: '2rem', fontSize: '1rem', lineHeight: 1.6 }}>Since this is a secure enterprise environment, we don't allow public signups directly via app for OAuth. Follow these steps to create a user securely.</p>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
              <div style={{ display: 'flex', gap: '1.5rem', alignItems: 'flex-start' }}>
                <div style={{ background: 'var(--primary)', color: 'white', width: '40px', height: '40px', borderRadius: '50%', display: 'grid', placeItems: 'center', fontWeight: 700, flexShrink: 0, fontSize: '1.2rem' }}>1</div>
                <div>
                  <h4 style={{ fontWeight: 700, marginBottom: '0.5rem', fontSize: '1.1rem', color: '#0f172a' }}>Click "Continue with Keycloak"</h4>
                  <p style={{ fontSize: '0.95rem', color: '#475569', lineHeight: 1.5 }}>This will redirect you to secure Keycloak authentication server.</p>
                </div>
              </div>
              <div style={{ display: 'flex', gap: '1.5rem', alignItems: 'flex-start' }}>
                <div style={{ background: 'var(--primary)', color: 'white', width: '40px', height: '40px', borderRadius: '50%', display: 'grid', placeItems: 'center', fontWeight: 700, flexShrink: 0, fontSize: '1.2rem' }}>2</div>
                <div>
                  <h4 style={{ fontWeight: 700, marginBottom: '0.5rem', fontSize: '1.1rem', color: '#0f172a' }}>Register a New Account</h4>
                  <p style={{ fontSize: '0.95rem', color: '#475569', lineHeight: 1.5 }}>On Keycloak page, click <b>"Don't have an account?"</b> and fill in your details. Make sure to <b>turn OFF "Temporary Password"</b> switch.</p>
                </div>
              </div>
              <div style={{ display: 'flex', gap: '1.5rem', alignItems: 'flex-start' }}>
                <div style={{ background: 'var(--primary)', color: 'white', width: '40px', height: '40px', borderRadius: '50%', display: 'grid', placeItems: 'center', fontWeight: 700, flexShrink: 0, fontSize: '1.2rem' }}>3</div>
                <div>
                  <h4 style={{ fontWeight: 700, marginBottom: '0.5rem', fontSize: '1.1rem', color: '#0f172a' }}>Return to TrustAuth</h4>
                  <p style={{ fontSize: '0.95rem', color: '#475569', lineHeight: 1.5 }}>After creating account, simply log in with your new credentials. You will be automatically added to our system with "User" role.</p>
                </div>
              </div>
            </div>

            <div style={{ marginTop: '2.5rem', padding: '1.25rem', background: '#eff6ff', border: '1px solid #bfdbfe', borderRadius: '12px', fontSize: '0.95rem', color: '#1e3a8a', fontWeight: 500, display: 'flex', gap: '10px', alignItems: 'center' }}>
              <span>ðŸ’¡</span> <span><b>Tip:</b> An admin can later upgrade your role to "Manager" or "Admin" from Dashboard.</span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Auth;
