import React, { useState } from 'react';
import api from '../api';
import '../App.css';

const Dashboard = ({ user, onLogout }) => {
  const [users, setUsers] = useState([]);
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [view, setView] = useState('dashboard');

  // State for password change
  const [newPassword, setNewPassword] = useState('');
  const [showChangePassId, setShowChangePassId] = useState(null); // Tracks which user is changing password

  const fetchAdminUsers = async () => {
    setLoading(true);
    try {
      const res = await api.get('/admin/users');
      setUsers(res.data);
    } catch (err) {
      alert("Access Denied.");
    } finally {
      setLoading(false);
    }
  };

  // NEW: Handle Password Change
  const handleUpdatePassword = async () => {
    if (!newPassword || newPassword.length < 6) return;
    
    try {
      await api.put(`/admin/users/${showChangePassId}/password`, { password: newPassword });
      alert('Password updated successfully!');
      setShowChangePassId(null);
      setNewPassword('');
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to update password');
    }
  };

  // NEW: Handle Delete User
  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await api.delete(`/admin/users/${userId}`);
        fetchAdminUsers(); // Refresh list
      } catch (err) {
        alert(err.response?.data?.message || 'Failed to delete user');
      }
    }
  };

  const handleRoleChange = async (userId, newRole, provider) => {
    // CONSTRAINT: Keycloak users cannot change roles from Dashboard
    if (provider === 'keycloak') {
      alert("Cannot change Keycloak user roles via Dashboard.");
      return;
    }

    try {
      await api.put(`/admin/users/${userId}/role`, { role: newRole });
      fetchAdminUsers();
    } catch (err) {
      alert('Failed to update role');
    }
  };

  const filteredUsers = users.filter(u => 
    u.username.toLowerCase().includes(searchTerm.toLowerCase()) || 
    u.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Keycloak Signup URL (Direct Access Grant)
  const handleKeycloakSignup = () => {
    window.location.href = `http://localhost:8080/realms/r-realm/protocol/openid-connect/registrations?client_id=r-client&response_type=code&scope=openid email profile`;
  };

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        
        {/* NEW: LOGO AREA */}
        <div className="trust-logo">
          {/* TODO: Replace 'src' below with 'logo.png' once you place it in client/public/ */}
          <img src="https://placehold.co/400x400/png" alt="TrustAuth Logo" />
        </div>

        <div className="brand">
          <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
            <path d="M12 22s8-4 8-10V5l-8-3-8 3v12s8 4 8 10z"/>
          </svg>
        </div>
        
        <div 
          className={`nav-item ${view === 'dashboard' ? 'active' : ''}`} 
          onClick={() => setView('dashboard')}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><rect x="3" y="3" width="7" height="7"/><rect x="14" y="3" width="7" height="7"/><rect x="14" y="14" width="7" height="7"/><rect x="3" y="14" width="7" height="7"/></svg>
          Dashboard
        </div>
        
        {user.role === 'admin' && (
          <div 
            className={`nav-item ${view === 'users' ? 'active' : ''}`} 
            onClick={() => setView('users')}
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"/><circle cx="9" cy="7" r="4"/><path d="M23 21v-2a4 4 0 0 0-3-3.87"/><path d="M16 3.13a4 4 0 0 1 0 7.75"/></svg>
            User Management
          </div>
        )}

        {user.role === 'admin' && (
          <div 
            className={`nav-item ${view === 'logs' ? 'active' : ''}`} 
            onClick={() => setView('logs')}
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"/><polyline points="14 2 14 8 20 8"/><line x1="16" y1="13" x2="8" y2="13"/><line x1="16" y1="17" x2="8" y2="17"/><polyline points="10 9 9 9 8 9"/></svg>
            Activity Logs
          </div>
        )}

        <div className="sidebar-bottom">
          <div className="user-profile">
            <div className="avatar">{user.username.charAt(0).toUpperCase()}</div>
            <div style={{ flex: 1, overflow: 'hidden' }}>
              <div style={{ fontWeight: 700, fontSize: '0.95rem', textOverflow: 'ellipsis', overflow: 'hidden', whiteSpace: 'nowrap' }}>{user.username}</div>
              <div style={{ fontSize: '0.8rem', color: '#94a3b8', fontWeight: 500 }}>
                {user.provider === 'keycloak' ? 'Keycloak' : 'Local'}
              </div>
            </div>
            <button onClick={() => onLogout(user)} style={{ background: 'none', border: 'none', color: '#94a3b8', cursor: 'pointer', padding: '5px', transition: '0.2s' }} title="Logout">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"/><polyline points="16 17 21 12 16 7"/><line x1="21" y1="12" x2="9" y2="12"/></svg>
            </button>
          </div>
          
          {/* NEW: SELF-SERVICE LINK (Only Local Admins) */}
          {user.role === 'admin' && user.provider !== 'keycloak' && (
            <div style={{ marginTop: '1rem', borderTop: '1px solid #e2e8f0', paddingTop: '1rem' }}>
              <p style={{ fontSize: '0.75rem', color: '#94a3b8', fontWeight: 600, marginBottom: '0.5rem', textTransform: 'uppercase', letterSpacing: '0.05em' }}>Self-Service</p>
              <a 
                onClick={handleKeycloakSignup} 
                style={{ display: 'flex', alignItems: 'center', gap: '6px', fontSize: '0.9rem', color: 'var(--primary)', fontWeight: 600, cursor: 'pointer', textDecoration: 'none', transition: '0.2s' }}
              >
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M16 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"/><circle cx="12" cy="7" r="4"/><line x1="12" y1="3" x2="12" y2="21"/><path d="M12 7v4"/><path d="M12 11h.01"/></svg>
                Sign up with Keycloak
              </a>
          </div>
        )}
        </div>
      </aside>

      <main className="main-content">
        <div className="top-bar">
          <div className="welcome-text">
            <h2>Hello, {user.username} </h2>
            <p>Welcome back! Here is what's happening today.</p>
          </div>
          {(view === 'users' || view === 'logs') && (
            <div className="search-bar">
              <span className="search-icon"></span>
              <input type="text" placeholder="Search users, emails..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
            </div>
          )}
        </div>

        {view === 'dashboard' && (
          <div className="grid-3">
            <div className="card stat-card">
              <div className="stat-icon"></div>
              <div className="stat-value">{users.length}</div>
              <div className="stat-label">Total Users</div>
            </div>
            <div className="card stat-card">
              <div className="stat-icon" style={{ color: '#10b981', background: '#d1fae5' }}></div>
              <div className="stat-value">Active</div>
              <div className="stat-label">Security Status</div>
            </div>
            <div className="card stat-card">
              <div className="stat-icon" style={{ color: '#f59e0b', background: '#fef3c7' }}></div>
              <div className="stat-value">99.9%</div>
              <div className="stat-label">System Uptime</div>
            </div>
          </div>
        )}

        {view === 'users' && user.role === 'admin' && (
          <div className="card" style={{ padding: '2rem' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
              <div>
                <h3 style={{ fontSize: '1.5rem', fontWeight: 700, color: '#0f172a' }}>User Management</h3>
                <p style={{ fontSize: '0.95rem', color: '#64748b', marginTop: '4px', fontWeight: 500 }}>Manage local users only. Keycloak users are hidden.</p>
              </div>
              <button className="btn btn-primary btn-sm" onClick={fetchAdminUsers}>
                {loading ? 'Loading...' : 'Refresh List'}
              </button>
            </div>
            
            <div className="table-wrapper">
              <table>
                <thead>
                  <tr><th>User Profile</th><th>Email Address</th><th>Provider</th><th>Current Role</th><th style={{ textAlign: 'right' }}>Actions</th></tr>
                </thead>
                <tbody>
                  {filteredUsers.map(u => (
                    <tr key={u._id}>
                      <td>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                          <div className="avatar" style={{ width: '36px', height: '36px', fontSize: '0.9rem' }}>{u.username.charAt(0)}</div>
                          <span style={{ fontWeight: 600 }}>{u.username}</span>
                        </div>
                      </td>
                      <td style={{ color: '#64748b', fontWeight: 500 }}>{u.email}</td>
                      <td>
                        {/* PROVIDER BADGE */}
                        {u.provider === 'keycloak' && (
                          <span className="badge" style={{ background: '#e0f2fe', color: '#0369a1' }}>Keycloak</span>
                        )}
                        {u.provider === 'local' && (
                          <span className="badge" style={{ background: '#d1fae5', color: '#065f46' }}>Local</span>
                        )}
                      </td>
                      <td><span className={`badge badge-${u.role}`}>{u.role}</span></td>
                      <td style={{ textAlign: 'right', display: 'flex', gap: '8px', justifyContent: 'flex-end' }}>
                        
                        {/* CHANGE PASSWORD (Key Icon) */}
                        {u.provider === 'local' && (
                          <button 
                            onClick={() => setShowChangePassId(u._id)}
                            style={{ background: 'none', border: 'none', color: '#64748b', cursor: 'pointer', padding: '4px', borderRadius: '4px', transition: '0.2s' }}
                            title="Change Password"
                          >
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><rect x="3" y="11" width="18" height="10" rx="2"/><path d="M7 11v-1a2 2 0 0 0 2-2h2a2 2 0 0 0 2 2v1a2 2 0 0 0 2-2h2a2 2 0 0 0 2 2v1a2 2 0 0 0 2 2v-6a2 2 0 0 0-2-2h2a2 2 0 0 0 2 2v12a2 2 0 0 0 2 2h2a2 2 0 0 0 2-2v8a2 2 0 0 0 2 2-2h2a2 2 0 0 0 2 2v8a2 2 0 0 0 2-2h2a2 2 0 0 0 2-2v8a2 2 0 0 0 2 2z"/></svg>
                          </button>
                        )}

                        {/* CHANGE ROLE (Dropdown) */}
                        {u.provider === 'local' ? (
                          <select 
                            value={u.role} 
                            onChange={(e) => handleRoleChange(u._id, e.target.value, u.provider)}
                            style={{ padding: '8px 16px', borderRadius: '8px', border: '2px solid #e2e8f0', outline: 'none', fontWeight: 600, cursor: 'pointer', background: 'white' }}
                          >
                            <option value="user">User</option>
                            <option value="manager">Manager</option>
                            <option value="admin">Admin</option>
                          </select>
                        ) : (
                          <span style={{ fontSize: '0.8rem', color: '#94a3b8', fontStyle: 'italic' }}>Managed via Keycloak</span>
                        )}

                        {/* DELETE BUTTON (Trash) */}
                        {u.provider === 'local' && (
                          <button 
                            onClick={() => handleDeleteUser(u._id)}
                            style={{ background: 'none', border: 'none', color: '#ef4444', cursor: 'pointer', padding: '4px', borderRadius: '4px', transition: '0.2s' }}
                            title="Delete User"
                          >
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><polyline points="3 6 6 7.66 10.66 18.66 14.1 21.49 21.49 7.66 14.1 3 6 7.66 18.66 21.49 15.56 17.78 8.5"/><path d="M23 4H6v16a2 2 0 0 0-2 2h16v-2a2 2 0 0 0 2-2H4a2 2 0 0 0-2 2V4z"/><line x1="9" y1="10" x2="15" y2="10"/></svg>
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                  {filteredUsers.length === 0 && (
                    <tr><td colSpan="6" style={{ textAlign: 'center', padding: '3rem', color: '#94a3b8', fontWeight: 500 }}>
                      {loading ? <div className="skeleton" style={{ height: '20px', width: '150px', margin: '0 auto' }}></div> : 'No local users found.'}
                    </td></tr>
                  )}
                </tbody>
              </table>

              {/* Password Change Modal */}
              {showChangePassId && (
                <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(15, 23, 42, 0.6)', zIndex: 100, display: 'grid', placeItems: 'center' }}>
                  <div className="card" style={{ padding: '2rem', width: '400px', borderRadius: '20px' }}>
                    <h3 style={{ marginBottom: '1rem', color: '#0f172a' }}>Change Password</h3>
                    <input 
                      type="password" 
                      placeholder="New Password" 
                      value={newPassword} 
                      onChange={(e) => setNewPassword(e.target.value)} 
                      style={{ width: '100%', padding: '0.8rem', marginBottom: '1.5rem', borderRadius: '8px', border: '1px solid #cbd5e1', outline: 'none' }}
                    />
                    <div style={{ display: 'flex', gap: '10px', justifyContent: 'flex-end' }}>
                      <button className="btn" style={{ background: '#64748b' }} onClick={() => setShowChangePassId(null)}>Cancel</button>
                      <button className="btn btn-primary" onClick={handleUpdatePassword}>Update</button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {view === 'logs' && user.role === 'admin' && (
          <div className="card" style={{ padding: '2rem' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
              <div>
                <h3 style={{ fontSize: '1.5rem', fontWeight: 700, color: '#0f172a' }}>Activity Logs</h3>
                <p style={{ fontSize: '0.95rem', color: '#64748b', marginTop: '4px', fontWeight: 500 }}>Track system events and logins.</p>
              </div>
              <button className="btn btn-primary btn-sm" onClick={fetchLogs}>{loading ? 'Loading...' : 'Refresh Logs'}</button>
            </div>

            <div className="table-wrapper">
              <table>
                <thead>
                  <tr><th>Timestamp</th><th>User</th><th>Activity Type</th><th>IP Address</th></tr>
                </thead>
                <tbody>
                  {logs.map((log, idx) => (
                    <tr key={idx}>
                      <td style={{ fontSize: '0.9rem', color: '#64748b', fontWeight: 500 }}>
                        {new Date(log.timestamp).toLocaleString()}
                      </td>
                      <td style={{ fontWeight: 600 }}>{log.username}</td>
                      <td><span className={`badge ${log.action === 'LOGIN_SUCCESS' ? 'badge-admin' : 'badge-user'}`}>{log.action}</span></td>
                      <td style={{ fontFamily: 'monospace', color: '#94a3b8', fontSize: '0.85rem' }}>{log.ip}</td>
                    </tr>
                  ))}
                  {logs.length === 0 && (
                    <tr><td colSpan="4" style={{textAlign:'center', padding:'3rem', color:'#94a3b8', fontWeight:500 }}>No logs found.</td></tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
        
        {view !== 'dashboard' && user.role !== 'admin' && (
          <div className="card" style={{ padding: '4rem', textAlign: 'center' }}>
            <div style={{ fontSize: '3.5rem', marginBottom: '1rem' }}></div>
            <h3 style={{ fontSize: '1.5rem', marginBottom: '0.5rem', color: '#0f172a' }}>Restricted Access</h3>
            <p style={{ color: '#64748b', maxWidth: '500px', margin: '0 auto', fontSize: '1.05rem', lineHeight: '1.6' }}>
              You do not have necessary permissions to view this section. Please contact an administrator if you believe this is an error.
            </p>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
